pragma solidity ^0.4.10;
contract Token{
	uint totalSupply=5000;
   //create the map to provide the ether
   mapping(address => uint) balanceEther;
   //create the method to provide PQD token
   mapping(address => uint) balanceOf;
   function PQD() public{
        balanceOf[msg.sender]=totalSupply;
   }
   //create the event to show the data of transaction
   event trade(address from, address to, uint amount);
   //create the event to show the data of  ethereum
   event deposit(address owner, uint amount);
   //create the method to deposit ether
   function depositEther(address acc) public payable{
       require(acc!=msg.sender);
       balanceEther[acc]=acc.balance;
       deposit(acc, balanceEther[acc]);
   }
   //create the method to buy PQD token
   function buyPQD(address owner, uint amount) public{
       require(owner!=msg.sender);
       require(balanceOf[msg.sender]>amount);
       require(balanceEther[owner]>amount);
       balanceOf[msg.sender]-=amount;
       balanceOf[owner]+=amount;
       balanceEther[owner]-=amount;
       deposit(owner,balanceEther[owner]);
       trade(msg.sender,owner,amount);
   }
   //create the method to get balance of the account
   function getWallet(address owner) public constant returns(uint){
       return balanceOf[owner];
   }
    //create the method to check Balance of Ether
   function checkEther(address acc) public constant returns(uint){
       return balanceEther[acc];
   }
   //create the method to trade between to account
   function exchange(address seller, address buyer, uint amount) public{
       require(seller!=msg.sender);
       require(balanceOf[seller]>amount);
       balanceOf[seller]-=amount;
       balanceEther[seller]+=amount;
       balanceOf[buyer]+=amount;
       trade(seller,buyer,amount);
   }
    
}
